mapper.select:
SELECT * FROM( SELECT ROWNUM WR, RN.* FROM ( SELECT ROWNUM RN, c.id, c.description, c.user_id as userId, c.create_time as createTime, c.status, c.blog_id as blogId, c.reply_userId as replyUserId, c.parent_id, ku.username, ku.header_pic as headerPic FROM krry_comment c LEFT JOIN krry_blog_user ku ON ku.id = c.reply_userId
and c.status = #{status}
and c.blog_id = #{blogId}
ORDER BY c.id desc )RN )WR WHERE WR <= #{pageSize} AND WR > #{pageNo}

SELECT * FROM(	
	SELECT ROWNUM WR, RN.* FROM (	
		SELECT ROWNUM RN,
			c.id,
			c.description,
			c.user_id as userId,
			c.create_time as createTime,
			c.status,
			c.blog_id as blogId,
			c.reply_userId as replyUserId,
			c.parent_id,
			ku.username,
			ku.header_pic as headerPic
		FROM
			krry_comment c
			LEFT JOIN krry_blog_user ku ON ku.id = c.reply_userId


					and c.status = #{status}


					and c.description = #{description}


					and c.create_time = #{createTime}


					and c.user_id = #{userId}


					and c.blog_id = #{blogId}


					and c.reply_userId = #{replyUserId}


			ORDER BY c.id desc
		)RN
	)WR
	WHERE WR <= 1 AND WR > 0

mapper.update:
UPDATE krry_blog
title =#{title}, description=#{description}, update_time=#{updateTime}, user_id=#{userId}, is_delete=#{isDelete}, is_banner=#{isBanner}, is_top=#{isTop}, is_recom=#{isRecom}, is_delete_lrc=#{isDeleteLrc}, status=#{status}, hits=#{hits}, img=#{img}, category_id=#{categoryId}, musiclink =#{musiclink}, musiclrc =#{musiclrc}, musiclrclink =#{musiclrclink}, musictor =#{musictor}, musictitle =#{musictitle}, musicsize =#{musicsize}, musictime =#{musictime},
WHERE id = #{id}
