mapper.select:
SELECT * FROM(	
	SELECT ROWNUM WN,RN.* FROM (	
		SELECT 
			,
			count(km.blog_id) as comments
		FROM
			krry_blog c
			LEFT JOIN krry_category kc ON kc.id = c.category_id
			LEFT JOIN krry_blog_user ku ON ku.id = c.user_id
			LEFT JOIN krry_comment km ON km.blog_id = c.id


				and c.id != #{filterId}


				and c.status = #{status}


				and c.is_delete = #{isDelete}


				and c.isTop = #{isTop}


				and RN.isRecom = #{isRecom}


				and c.categoryId = #{typeId}


				and ku.id = #{userId}


		GROUP BY 
		ORDER BY c.create_time desc
	)RN
)WN
WHERE WN <= #{pageSize} AND WN > #{pageNo}

SELECT
	count(*)
FROM
	krry_blog c
	LEFT JOIN krry_category kc ON kc.id = c.category_id
	LEFT JOIN krry_blog_user ku ON ku.id = c.user_id


			and c.id != #{filterId}


			and c.status = #{status}


			and c.is_delete = #{isDelete}


			and c.is_top = #{isTop}


			and RN.isRecom = #{isRecom}


			and c.category_id = #{typeId}


			AND ku.id = #{userId}

mapper.update:
UPDATE krry_blog
title =#{title}, description=#{description}, update_time=#{updateTime}, user_id=#{userId}, is_delete=#{isDelete}, is_banner=#{isBanner}, is_top=#{isTop}, is_recom=#{isRecom}, is_delete_lrc=#{isDeleteLrc}, status=#{status}, hits=#{hits}, img=#{img}, category_id=#{categoryId}, musiclink =#{musiclink}, musiclrc =#{musiclrc}, musiclrclink =#{musiclrclink}, musictor =#{musictor}, musictitle =#{musictitle}, musicsize =#{musicsize}, musictime =#{musictime},
WHERE id = #{id}
